SELECT abnomaly.*, user.user_id, user.user_name, window.window_id, window.window_name FROM (abnomaly INNER JOIN user ON abnomaly.user_hidden_id=user.user_hidden_id)
    INNER JOIN window ON abnomaly.window_hidden_id=window.window_hidden_id
WHERE abnomaly.abnomaly_hidden_id=#{VALUE }

