SELECT br_anomaly.*, br_user.user_id, br_user.user_name, br_window.window_id, br_window.window_name FROM (br_anomaly LEFT JOIN br_user ON br_anomaly.user_hidden_id=br_user.user_hidden_id)
    LEFT JOIN br_window ON br_anomaly.window_hidden_id=br_window.window_hidden_id
WHERE anomaly.anomaly_hidden_id=#{VALUE }

